import{_ as t,o as a,c as d,s as e}from"./index-1bfe471e.js";const f="/bdd/medias/schema_fk_1_n.png",v="/bdd/medias/schema_fk_n_n.png";const r={},n=e('<h2 data-v-7874f670>Pour une relation 1 à n</h2><p data-v-7874f670>Deux tables, T_Personnes :</p><table data-v-7874f670><tr data-v-7874f670><th data-v-7874f670>ID_Personne</th><th data-v-7874f670>Nom_pers</th><th data-v-7874f670>Prenom_pers</th><th data-v-7874f670>FK_Sexe</th><th data-v-7874f670>Etc.</th></tr><tr data-v-7874f670><td data-v-7874f670>1</td><td data-v-7874f670>Roger</td><td data-v-7874f670>Tom</td><td data-v-7874f670>1</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>2</td><td data-v-7874f670>Breton</td><td data-v-7874f670>Alexia</td><td data-v-7874f670>2</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>3</td><td data-v-7874f670>Leroy</td><td data-v-7874f670>Rémi</td><td data-v-7874f670>1</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>4</td><td data-v-7874f670>Blanchard</td><td data-v-7874f670>Amandine</td><td data-v-7874f670>2</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>5</td><td data-v-7874f670>Collin</td><td data-v-7874f670>Françoise</td><td data-v-7874f670>2</td><td data-v-7874f670></td></tr></table><p data-v-7874f670>Et T_Sexes :</p><table data-v-7874f670><tr data-v-7874f670><th data-v-7874f670>ID_Sexe</th><th data-v-7874f670>Nom_sexe</th></tr><tr data-v-7874f670><td data-v-7874f670>1</td><td data-v-7874f670>Homme</td></tr><tr data-v-7874f670><td data-v-7874f670>2</td><td data-v-7874f670>Femme</td></tr></table><p data-v-7874f670> Le champ FK_Sexe de T_Personnes prend comme valeur l&#39;ID du sexe de la personne. </p><p data-v-7874f670>Petit schéma :</p><img src="'+f+'" alt="Schéma 1 à n" data-v-7874f670><p data-v-7874f670>Ainsi chaque personne a un sexe attribué et les avantages sont :</p><ul data-v-7874f670><li data-v-7874f670>Juste un nombre à la place d&#39;un mot de 5 lettres à stocker</li><li data-v-7874f670> Modifier un élément de la table T_Sexes le modifiera pour tout le monde </li></ul><h2 data-v-7874f670>Pour une relation n à n</h2><p data-v-7874f670>Imaginons maintenant cet exemple. Nous gardons la table T_Personnes :</p><table data-v-7874f670><tr data-v-7874f670><th data-v-7874f670>ID_Personne</th><th data-v-7874f670>Nom_pers</th><th data-v-7874f670>Prenom_pers</th><th data-v-7874f670>FK_Sexe</th><th data-v-7874f670>Etc…</th></tr><tr data-v-7874f670><td data-v-7874f670>1</td><td data-v-7874f670>Roger</td><td data-v-7874f670>Tom</td><td data-v-7874f670>1</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>2</td><td data-v-7874f670>Breton</td><td data-v-7874f670>Alexia</td><td data-v-7874f670>2</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>3</td><td data-v-7874f670>Leroy</td><td data-v-7874f670>Rémi</td><td data-v-7874f670>1</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>4</td><td data-v-7874f670>Blanchard</td><td data-v-7874f670>Amandine</td><td data-v-7874f670>2</td><td data-v-7874f670></td></tr><tr data-v-7874f670><td data-v-7874f670>5</td><td data-v-7874f670>Collin</td><td data-v-7874f670>Françoise</td><td data-v-7874f670>2</td><td data-v-7874f670></td></tr></table><p data-v-7874f670>Et nous avons maintenant la table T_Emails :</p><table data-v-7874f670><tr data-v-7874f670><th data-v-7874f670>ID_Email</th><th data-v-7874f670>Adresse_email</th></tr><tr data-v-7874f670><td data-v-7874f670>1</td><td data-v-7874f670>yannis.burkhalter@normalux.ch</td></tr><tr data-v-7874f670><td data-v-7874f670>2</td><td data-v-7874f670>bdepsic@yahoo.fr</td></tr><tr data-v-7874f670><td data-v-7874f670>3</td><td data-v-7874f670>prenom.nom@gmail.com</td></tr><tr data-v-7874f670><td data-v-7874f670>4</td><td data-v-7874f670>info@esat.ch</td></tr><tr data-v-7874f670><td data-v-7874f670>5</td><td data-v-7874f670>info@normalux.ch</td></tr></table><p data-v-7874f670> Petit problème ; une adresse peut appartenir à plusieurs personnes et une personne peut avoir plusieurs adresses.<br data-v-7874f670>Nous ne pouvons donc pas mettre de champs FK dans une de ces deux tables. </p><p data-v-7874f670>Il va donc falloir créer une table intermédiaire T_Avoir_Email :</p><table data-v-7874f670><tr data-v-7874f670><th data-v-7874f670>ID_Avoir_Email</th><th data-v-7874f670>FK_Personne</th><th data-v-7874f670>FK_Email</th></tr><tr data-v-7874f670><td data-v-7874f670> </td><td data-v-7874f670></td><td data-v-7874f670></td></tr></table><p data-v-7874f670> Et à chaque fois qu&#39;une personne possède une adresse e-mail, nous allons ajouter l&#39;ID de la personne et l&#39;ID de l&#39;e-mail dans les champs ainsi : </p><table data-v-7874f670><tr data-v-7874f670><th data-v-7874f670>ID_Avoir_Email</th><th data-v-7874f670>FK_Personne</th><th data-v-7874f670>FK_Email</th></tr><tr data-v-7874f670><td data-v-7874f670>1</td><td data-v-7874f670>1</td><td data-v-7874f670>1</td></tr><tr data-v-7874f670><td data-v-7874f670>2</td><td data-v-7874f670>1</td><td data-v-7874f670>2</td></tr><tr data-v-7874f670><td data-v-7874f670>3</td><td data-v-7874f670>2</td><td data-v-7874f670>3</td></tr><tr data-v-7874f670><td data-v-7874f670>4</td><td data-v-7874f670>3</td><td data-v-7874f670>3</td></tr><tr data-v-7874f670><td data-v-7874f670>5</td><td data-v-7874f670>5</td><td data-v-7874f670>4</td></tr></table><p data-v-7874f670>Selon cet exemple :</p><ul data-v-7874f670><li data-v-7874f670> Roger Tom a 2 adresses : yannis.burkhalter@normalux.ch et bdepsic@yahoo.fr </li><li data-v-7874f670> Breton Alexia et Leroy Rémi se partagent l&#39;adresse prenom.nom@gmail.com </li><li data-v-7874f670>Collin Françoise a l&#39;adresse info@esat.ch</li><li data-v-7874f670> Blanchard Amandine n&#39;a aucune adresse et info@normalux.ch n&#39;appartient à personne. </li></ul><p data-v-7874f670>Ce schéma vous parlera peut-être un peu plus :</p><img src="'+v+'" alt="Schéma n à n" data-v-7874f670>',24),s=[n];function o(l,i){return a(),d("div",null,s)}const m=t(r,[["render",o],["__scopeId","data-v-7874f670"]]);export{m as default};
